      break;

    case 'tag':
      _fetch.get_tag(obj_id);
      break;

    case 'location':
      _fetch.get_location(obj_id);
      break;

    case 'geography':
      _fetch.get_geography(obj_id);
      break;

    default:
      console.log('bad media update');
      return;
    }
  }
